// Merge deal and contact into a unified onboarding profile
const deal = $('HubSpot — Get Deal Details').item.json;
const contact = $('HubSpot — Get Associated Contact').item.json;

const props = contact.properties || contact;
const dealProps = deal.properties || deal;

const profile = {
  deal_id: deal.id,
  deal_name: dealProps.dealname || 'Unbenannter Deal',
  deal_amount: dealProps.amount ? parseFloat(dealProps.amount) : 0,
  close_date: dealProps.closedate || new Date().toISOString(),
  owner_id: dealProps.hubspot_owner_id || null,

  contact_id: contact.id,
  firstname: props.firstname || '',
  lastname: props.lastname || '',
  email: props.email || '',
  phone: props.phone || '',
  company: props.company || '',
  jobtitle: props.jobtitle || '',
  city: props.city || '',

  company_type: props.company_type || 'Mittelstand',
  company_size: props.company_size || 'mittel',
  current_software: props.current_software || 'unbekannt',
  pain_points: props.pain_points || '',
  website: props.website || '',

  onboarding_date: new Date().toISOString(),
  full_name: `${props.firstname || ''} ${props.lastname || ''}`.trim()
};

return { json: profile };

