// Extract and parse the JSON from the AI agent's text output
const agentOutput = $input.first().json.output || $input.first().json.text || '';

let parsed;
try {
  // Strip markdown code fences if present
  const cleaned = agentOutput
    .replace(/^```json\s*/i, '')
    .replace(/^```\s*/i, '')
    .replace(/\s*```$/i, '')
    .trim();
  parsed = JSON.parse(cleaned);
} catch (err) {
  // Attempt to extract JSON block from mixed content
  const jsonMatch = agentOutput.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    parsed = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error(`AI Agent returned non-parseable output: ${agentOutput.slice(0, 200)}`);
  }
}

// Validate required keys
const required = ['welcome_email', 'calendar_event', 'notion_page', 'task_list', 'slack_summary'];
for (const key of required) {
  if (!parsed[key]) {
    throw new Error(`Missing required key in agent output: ${key}`);
  }
}

// Merge with upstream profile data
const profile = $('Code — Merge Deal and Contact').item.json;

return {
  json: {
    ...profile,
    onboarding_content: parsed
  }
};

const data = $input.first().json;
const masked = {
  ...data,
  phone: data.phone ? data.phone.slice(0, -4).replace(/./g, '*') + data.phone.slice(-4) : null,
  email_display: data.email ? data.email.replace(/(.{2})(.*)(@.*)/, '$1***$3') : null
};
return { json: masked };

